import { Component } from '@angular/core';



@Injectable({
  providedIn: 'root'
})

@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
export class QlspComponent {
  private watches: Watch[] = [
    { id: 1, name: 'Watch 1', brand: 'Brand A', price: 100 },
    { id: 2, name: 'Watch 2', brand: 'Brand B', price: 200 },
    { id: 3, name: 'Watch 3', brand: 'Brand C', price: 300 },
  ];

  getWatches(): Watch[] {
    return this.watches;
  }

  addWatch(watch: Watch) {
    this.watches.push(watch);
  }

  updateWatch(watch: Watch) {
    const index = this.watches.findIndex(w => w.id === watch.id);
    if (index !== -1) {
      this.watches[index] = watch;
    }
  }

  deleteWatch(id: number) {
    const index = this.watches.findIndex(w => w.id === id);
    if (index !== -1) {
      this.watches.splice(index, 1);
    }
  }
}

<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }
  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <div class="center">
		<p>Quản Lý Khách Hàng</p>
	</div>
    <h2>Watch List</h2>
    <h2>Watch List</h2>

    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Brand</th>
          <th>Price</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let watch of watches">
          <td>{{ watch.id }}</td>
          <td>{{ watch.name }}</td>
          <td>{{ watch.brand }}</td>
          <td>{{ watch.price }}</td>
          <td>
            <button (click)="deleteWatch(watch.id)">Delete</button>
            <button (click)="editWatch(watch)">Edit</button>
          </td>
        </tr>
      </tbody>
    </table>

    <hr>

    <h2>Add Watch</h2>

    <form (ngSubmit)="addWatch(name.value, brand.value, price.value)">
      <div>
        <label>Name:</label>
        <input #name required, placeholder="Watch name">
      </div>
      <div>
        <label>Brand:</label>
        <input #brand required, placeholder="Watch brand">
      </div>
      <div>
        <label>Price:</label>
        <input #price required, type="number", placeholder="Watch price">
      </div>
      <button type="submit">Add</button>
    </form>

    <hr>

    <h2>Edit Watch</h2>

    <form *ngIf="selectedWatch" (ngSubmit)="updateWatch(selectedWatch)">
      <div>
        <label>Name:</label>
        <input [(ngModel)]="selectedWatch.name" required, placeholder="Watch name">
      </div>
      <div>
        <label>Brand:</label>
        <input [(ngModel)]="selectedWatch.brand" required, placeholder="Watch brand">
      </div>
      <div>
        <label>Price:</label>
        <input [(ngModel)]="selectedWatch.price" required, type="number", placeholder="Watch price">
      </div>
      <button type="submit">Update</button>
    </form>
<body>
<html>
