import { Component } from '@angular/core';

interface DongHo {
  name: string;
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}









<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 100px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }












      h2 {
        margin-top: 0;
        margin-bottom: 20px;
      }

      mat-list-item {
        cursor: pointer;
      }

      mat-list-item.selected {
        background-color: #eee;
      }
  </style>
</head>
<!-- watch-form.component.html -->
<form (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="name">Tên sản phẩm:</label>
    <input type="text" class="form-control" id="name" [(ngModel)]="newWatch.name" name="name" required>
  </div>
  <div class="form-group">
    <label for="brand">Thương hiệu:</label>
    <input type="text" class="form-control" id="brand" [(ngModel)]="newWatch.brand" name="brand" required>
  </div>
  <div class="form-group">
    <label for="price">Giá:</label>
    <input type="number" class="form-control" id="price" [(ngModel)]="newWatch.price" name="price" required>
  </div>
  <div class="form-group">
    <label for="image">Ảnh:</label>
    <input type="text" class="form-control" id="image" [(ngModel)]="newWatch.image" name="image" required>
  </div>
  <button type="submit" class="btn btn-primary">Thêm sản phẩm</button>
</form>
<body>
<html>
