import { Component } from '@angular/core';



import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

interface Watch {
  id: number;
  name: string;
  price: number;
  description: string;
  image: string;
}

@Injectable({
  providedIn: 'root'
})



@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  private apiUrl = 'http://localhost:3000/watches';

  constructor(private http: HttpClient) {}

  getWatches(): Observable<Watch[]> {
    return this.http.get<Watch[]>(this.apiUrl);
  }

  addWatch(watch: Watch): Observable<Watch> {
    return this.http.post<Watch>(this.apiUrl, watch);
  }

  getWatch(id: number): Observable<Watch> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.get<Watch>(url);
  }

  updateWatch(id: number, watch: Watch): Observable<Watch> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.put<Watch>(url, watch);
  }

  deleteWatch(id: number): Observable<{}> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.delete(url);
  }
}
