import { Component} from '@angular/core';


@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  watches = [
    { name: 'Đồng hồ 1', price: 100, image: 'watch1.jpg' },
    { name: 'Đồng hồ 2', price: 200, image: 'watch2.jpg' },
    { name: 'Đồng hồ 3', price: 300, image: 'watch3.jpg' }
  ];
  newWatch = { name: '', price: 0, image: '' };
  showForm = false;

  toggleForm() {
    this.showForm = !this.showForm;
  }

  addWatch() {
    // kiểm tra xem người dùng đã nhập giá trị cho input image hay chưa
    if (this.newWatch.image.trim() === '') {
      alert('Vui lòng nhập đường dẫn hình ảnh cho sản phẩm!');
      return;
    }
    this.watches.push(this.newWatch);
    this.newWatch = { name: '', price: 0, image: '' };
    this.showForm = false;
  }
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>

  <div class="watch-list">
    <div class="watch" *ngFor="let watch of watches">
      <img [src]="'assets/images/' + watch.image" alt="{{ watch.name }}">
      <h3>{{ watch.name }}</h3>
      <p>{{ watch.price | currency }}</p>
    </div>
    <div class="add-watch" *ngIf="showForm">
      <h3>Thêm sản phẩm mới</h3>
      <form>
        <label for="name">Tên sản phẩm:</label>
        <input type="text" id="name" [(ngModel)]="newWatch.name">
        <label for="price">Giá tiền:</label>
        <input type="number" id="price" [(ngModel)]="newWatch.price">
        <label for="image">Hình ảnh:</label>
        <input type="text" id="image" [(ngModel)]="newWatch.image">
        <button type="button" (click)="addWatch()">Thêm sản phẩm</button>
      </form>
    </div>
    <button (click)="toggleForm()">{{ showForm ? 'Đóng' : 'Thêm sản phẩm mới' }}</button>
  </div>
<body>
<html>
