import { Component } from '@angular/core';

interface DongHo {
  name: string;
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  dongho: DongHo[] = [
    { name: 'Casio G-Shock' },
    { name: 'Seiko SKX007' },
    { name: 'Timex Weekender' },
    { name: 'Citizen Eco-Drive' },
    { name: 'Rolex Submariner' }
  ];

  selectedDongHo: DongHo | null = null;

  onSelect(dongho: DongHo): void {
    this.selectedDongHo = dongho;
  }
}









<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 100px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }












      h2 {
        margin-top: 0;
        margin-bottom: 20px;
      }

      mat-list-item {
        cursor: pointer;
      }

      mat-list-item.selected {
        background-color: #eee;
      }
  </style>
</head>
<h2>Quản lý danh sách đồng hồ</h2>

<!-- Danh sách đồng hồ -->
<ul>
  <li *ngFor="let watch of watches">{{watch.name}}</li>
</ul>

<!-- Form thêm đồng hồ mới -->
<form (submit)="addDongHo()">
  <label>
    Tên đồng hồ:
    <input type="text" [(ngModel)]="newDongHoName">
  </label>
  <button type="submit">Thêm</button>
</form>
<body>
<html>
