import { Component } from '@angular/core';
export interface Product {
  id: number;
  name: string;
  quantity: number;
  status: string;
  price: number;
  category: string;
}
@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {
  products: Product[] = [
    {
      id: 1,
      name: 'Product 1',
      quantity: 10,
      status: 'In stock',
      price: 100,
      category: 'Category 1'
    },
    {
      id: 2,
      name: 'Product 2',
      quantity: 5,
      status: 'Out of stock',
      price: 200,
      category: 'Category 2'
    },
    {
      id: 1,
      name: 'Product 3',
      quantity: 10,
      status: 'In stock',
      price: 100,
      category: 'Category 1'
    },
    {
      id: 2,
      name: 'Product 4',
      quantity: 5,
      status: 'Out of stock',
      price: 200,
      category: 'Category 2'
    },
    {
      id: 1,
      name: 'Product 5',
      quantity: 10,
      status: 'In stock',
      price: 100,
      category: 'Category 1'
    },
    {
      id: 2,
      name: 'Product 6',
      quantity: 5,
      status: 'Out of stock',
      price: 200,
      category: 'Category 2'
    },
    // Add more products here...
  ];

  deleteProduct(id: number) {
    // ...
  }

  editProduct(id: number) {
    // Define the logic for editing a product here
  }

  addProduct(newProduct: Product) {
    this.products.push(newProduct);
  }
}

<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>


</head>
<body>
  <div class="button-group">
    <a routerLink="/themsua" class="button">Tạo mới khách hàng</a>
    <a routerLink="/danhmuc" class="button">Xem Giá Sản Phẩm</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <table>
    <thead>
      <tr>
        <th>Mã sản phẩm</th>
        <th>Tên sản phẩm</th>
        <th>Số lượng</th>
        <th>Tình trạng</th>
        <th>Giá tiền</th>
        <th>Danh mục</th>
        <th>Chức năng</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of products">
        <td>{{product.id}}</td>
        <td>{{product.name}}</td>
        <td>{{product.quantity}}</td>
        <td>{{product.status}}</td>
        <td>{{product.price}}</td>
        <td>{{product.category}}</td>
        <td>
          <button (click)="editProduct(product.id)">Sửa</button>
          <button (click)="deleteProduct(product.id)">Xóa</button>
        </td>
      </tr>
    </tbody>
  </table>
<body>
<html>

<router-outlet></router-outlet>
