import { Component } from '@angular/core';

@Component({
  selector: 'app-danhmuc',
  templateUrl: './danhmuc.component.html',
  styleUrls: ['./danhmuc.component.css']
})
export class DanhmucComponent {

}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
  <script>
    const form = document.querySelector('#product-form');
    const table = document.querySelector('#product-table tbody');

    form.addEventListener('submit', (event) => {
      // Prevent default form submission
      event.preventDefault();

      // Get form values
      const name = document.querySelector('#name').value;
      const price = document.querySelector('#price').value;
      const quantity = document.querySelector('#quantity').value;
      const image = document.querySelector('#image').value;

      // Create new table row with product information
      const newRow = table.insertRow();
      const nameCell = newRow.insertCell(0);
      const priceCell = newRow.insertCell(1);
      const quantityCell = newRow.insertCell(2);
      const imageCell = newRow.insertCell(3);
      nameCell.textContent = name;
      priceCell.textContent = price;
      quantityCell.textContent = quantity;
      if (image) {
        const img = document.createElement('img');
        img.src = image;
        img.alt = name;
        img.width = 100;
        imageCell.appendChild(img);
      }

      // Reset form
      form.reset();
    });
  </script>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }
      .form-container {
        max-width: 800px;
        margin: 0 auto;
        padding: 20px;
      }

      #product-form h2 {
        margin-bottom: 10px;
      }

      #product-form label {
        display: block;
        margin-bottom: 5px;
      }

      #product-form input,
      #product-form button {
        display: block;
        margin-bottom: 10px;
        padding: 5px;
        width: 100%;
        box-sizing: border-box;
      }

      #product-form button {
        background-color: #4CAF50;
        color: white;
        border: none;
        border-radius: 5px;
        cursor: pointer;
      }

      #product-form button:hover {
        background-color: #3e8e41;
      }

      #product-table {
        margin-top: 20px;
        border-collapse: collapse;
        width: 100%;
      }

      #product-table th, #product-table td {
        padding: 10px;
        text-align: left;
        border-bottom: 1px solid #ddd;
      }

      #product-table th {
        background-color: #4CAF50;
        color: white;
      }
  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>

  <div class="form-container">
    <form id="product-form">
      <h2>Thông tin sản phẩm</h2>
      <label for="name">Tên sản phẩm:</label>
      <input type="text" id="name" name="name" required>

      <label for="price">Giá:</label>
      <input type="number" id="price" name="price" min="0" required>

      <label for="quantity">Số lượng:</label>
      <input type="number" id="quantity" name="quantity" min="1" required>

      <label for="image">Hình ảnh:</label>
      <input type="file" id="image" name="image">

      <button type="submit">Đăng ký sản phẩm</button>
    </form>

    <table id="product-table">
      <thead>
        <tr>
          <th>Tên sản phẩm</th>
          <th>Giá</th>
          <th>Số lượng</th>
          <th>Hình ảnh</th>
        </tr>
      </thead>
      <tbody>
      </tbody>
    </table>
  </div>

  <script src="script.js"></script>
<body>
<html>
