import { Component } from '@angular/core';
export interface Product {
  id: number;
  name: string;
  quantity: number;
  status: string;
  price: number;
  category: string;
}
@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {
  products: Product[] = [
    {
      id: 1,
      name: 'Product 1',
      quantity: 10,
      status: 'In stock',
      price: 100,
      category: 'Category 1'
    },
    {
      id: 2,
      name: 'Product 2',
      quantity: 5,
      status: 'Out of stock',
      price: 200,
      category: 'Category 2'
    },
    {
      id: 1,
      name: 'Product 3',
      quantity: 10,
      status: 'In stock',
      price: 100,
      category: 'Category 1'
    },
    {
      id: 2,
      name: 'Product 4',
      quantity: 5,
      status: 'Out of stock',
      price: 200,
      category: 'Category 2'
    },
    {
      id: 1,
      name: 'Product 5',
      quantity: 10,
      status: 'In stock',
      price: 100,
      category: 'Category 1'
    },
    {
      id: 2,
      name: 'Product 6',
      quantity: 5,
      status: 'Out of stock',
      price: 200,
      category: 'Category 2'
    },
    // Add more products here...
  ];

  deleteProduct(id: number) {
    // remove item out the list by id
    // tìm vị trí của phần tử có id trong mảng
  const index = this.products.findIndex(product => product.id === id);

  // kiểm tra nếu phần tử tồn tại trong mảng thì xóa nó
  if (index !== -1) {
    this.products.splice(index, 1);
  }
  }

  editProduct(id: number, newData: any) {
    // Define the logic for editing a product here 
    // get item by id then update data by id
    // tìm vị trí của sản phẩm có id trong mảng
  const index = this.products.findIndex(product => product.id === id);

  // kiểm tra nếu sản phẩm tồn tại trong mảng thì thay đổi thông tin của nó
  if (index !== -1) {
    // truy cập đến đối tượng sản phẩm và cập nhật các thông tin mới
    const product = this.products[index];
    Object.assign(product, newData);
  }
  }

  addProduct(newProduct: Product) {
    this.products.push(newProduct);
  }
}
