import { Component, OnInit  } from '@angular/core';
import { Product, ProductList } from './product-list';

@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  name = '';
  description = '';
  price = 0;

  constructor(private productList: ProductList) {}

  registerProduct() {
    const newProduct = new Product(this.name, this.description, this.price);
    this.productList.addProduct(newProduct);
    this.name = '';
    this.description = '';
    this.price = 0;
  }
}
