import { Component } from '@angular/core';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css">
	<title></title>
    <script>
        $(document).ready(function() {
            // Load customer list on page load
            loadCustomerList();

            // Add customer
            $("#add-customer-save").click(function() {
                var name = $("#add-customer-name").val();
                var email = $("#add-customer-email").val();
                var phone = $("#add-customer-phone").val();
                $.ajax({
                    url: "add_customer.php",
                    type: "POST",
                    data: {
                        name: name,
                        email: email,
                        phone: phone
                    },
                    success: function() {
                        $("#add-customer-modal").modal("hide");
                        loadCustomerList();
                    }
                });
            });

            // Edit customer
            var customer_id = "";
            $(document).on("click", ".edit-btn", function() {
                customer_id = $(this).attr("id");
                var name = $("#" + customer_id + "-name").text();
                var email = $("#" + customer_id + "-email").text();
                var phone = $("#" + customer_id + "-phone").text();
                $("#edit-customer-name").val(name);
                $("#edit-customer-email").val(email);
                $("#edit-customer-phone").val(phone);
            });
            $("#edit-customer-save").click(function() {
                var name = $("#edit-customer-name").val();
                var email = $("#edit-customer-email").val();
                var phone = $("#edit-customer-phone").val();
                $.ajax({
                    url: "edit_customer.php",
                    type: "POST",
                    data: {
                        id: customer_id,
                        name: name,
                        email: email,
                        phone: phone
                    },
                    success: function() {
                        $("#edit-customer-modal").modal("hide");
                        loadCustomerList();
                    }
                });
            });

            // Delete customer
            $(document).on("click", ".delete-btn", function() {
                customer_id = $(this).attr("id");
            });
            $("#delete-customer-confirm").click(function() {
                $.ajax({
                    url: "delete_customer.php",
                    type: "POST",
                    data: {
                        id: customer_id
                    },
                    success: function() {
                        $("#delete-customer-modal").modal("hide");
                        loadCustomerList();
                    }
                });
            });
        });

        function loadCustomerList() {
            $.ajax({
                url: "get_customer_list.php",
                type: "GET",
                dataType: "json",
                success: function(data) {
                    var html = "";
                    $.each(data, function(key, value) {
                        html += "<tr>";
                        html += "<td>" + value.id + "</td>";
                        html += "<td id='" + value.id + "-name'>" + value.name + "</td>";
                        html += "<td id='" + value.id + "-email'>" + value.email + "</td>";
                        html += "<td id='" + value.id + "-phone'>" + value.phone + "</td>";
                        html += "<td>";
                        html += "<button class='btn btn-sm btn-info edit-btn' id='" + value.id + "' data-toggle='modal' data-target='#edit-customer-modal'>Edit</button> ";
                        html += "<button class='btn btn-sm btn-danger delete-btn' id='" + value.id + "' data-toggle='modal' data-target='#delete-customer-modal'>Delete</button>";
                        html += "</td>";
                        html += "</tr>";
                    });
                    $("#customer-list").html(html);
                }
            });
        }

    </script>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }
  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <div class="center">
		<p>Quản Lý Khách Hàng</p>
	</div>
    <div class="container">
        <h1>Customer List</h1>
        <button class="btn btn-primary" data-toggle="modal" data-target="#add-customer-modal">Add Customer</button>
        <br><br>
        <table class="table table-striped">
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody id="customer-list">
            </tbody>
        </table>
    </div>

    <div class="modal fade" id="add-customer-modal" tabindex="-1" role="dialog" aria-labelledby="add-customer-modal-title" aria-hidden="true">
        <div class="modal-dialog" role="document">
            <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title" id="add-customer-modal-title">Add Customer</h5>
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div class="modal-body">
                    <form>
                        <div class="form-group">
                            <label for="add-customer-name">Name:</label>
                            <input type="text" class="form-control" id="add-customer-name" required>
                        </div>
                        <div class="form-group">
                            <label for="add-customer-email">Email:</label>
                            <input type="email" class="form-control" id="add-customer-email" required>
                        </div>
                        <div class="form-group">
                            <label for="add-customer-phone">Phone:</label>
                            <input type="tel" class="form-control" id="add-customer-phone" required>
                        </div>
                    </form>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-dismiss="modal">Cancel</button>
                    <button type="button" class="btn btn-primary" id="add-customer-save">Save</button>
                </div>
            </div>
        </div>
    </div>

    <div class="modal fade" id="edit-customer-modal" tabindex="-1" role="dialog" aria-labelledby="edit-customer-modal-title" aria-hidden="true">
        <div class="modal-dialog" role="document">
            <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title" id="edit-customer-modal-title">Edit Customer</h5>
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div class="modal-body">
                    <form>
                        <div class="form-group">
                            <label for="edit-customer-name">Name:</label>
                            <input type="text" class="form-control" id="edit-customer-name" required>
                        </div>
                        <div class="form-group">
                            <label for="edit-customer-email">Email:</label>
                            <input type="email" class="form-control" id="edit-customer-email" required>
                        </div>
                        <div class="form-group">
                            <label for="edit-customer-phone">Phone:</label>
                            <input type="tel" class="form-control" id="edit-customer-phone" required>
                        </div>
                    </form>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-dismiss="modal">Cancel</button>
                    <button type="button" class="btn btn-primary" id="edit-customer-save">Save</button>
                </div>
           div>
    </div>

    <script src="https://code.jquery.com/jquery-3.2.1.slim.min.js"></script>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/js/bootstrap.min.js"></script>


    <div class="modal fade" id="delete-customer-modal" tabindex="-1" role="dialog" aria-labelledby="delete-customer-modal-title" aria-hidden="true">
        <div class="modal-dialog" role="document">
            <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title" id="delete-customer-modal-title">Delete Customer</h5>
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div class="modal-body">
                    <p>Are you sure you want to delete this customer?</p>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-dismiss="modal">Cancel</button>
                    <button type="button" class="btn btn-danger" id="delete-customer-confirm">Confirm</button>
                </div>
            </div>
        </div>
    </div>
<body>
<html>
