import { Component , Input} from '@angular/core';

import { Watch } from '../../../models/watch';
@Component({
  selector: 'app-spham',
  templateUrl: './spham.component.html',
  styleUrls: ['./spham.component.css']
})
export class SphamComponent {
  @Input() watch: Watch;

  addToCart(watch: Watch) {
    // implementation of addToCart method
  }
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

</head>
<body>
  <div class="button-group">
    <a routerLink="/home" class="button">Tạo mới Sản Phẩm</a>
    <a routerLink="/list" class="button">Thêm danh mục</a>
    <a routerLink="/danhmuc" class="button">In dữ liệu</a>


  </div>
  <div class="watch">
    <img [src]="watch.image" alt="{{ watch.name }}" class="watch-image">
    <div class="watch-details">
      <h2 class="watch-name">{{ watch.name }}</h2>
      <p class="watch-price">{{ watch.price | currency }}</p>
      <button (click)="addToCart(watch)" class="watch-add-to-cart">Add to Cart</button>
    </div>
  </div>
<body>
<html>
