@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  products = [
    {
      brand: 'Rolex',
      model: 'Submariner',
      price: 5000000,
      imageUrl: 'https://images.pexels.com/photos/125779/pexels-photo-125779.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1/150x150'
    },
    {
      brand: 'Omega',
      model: 'Seamaster',
      price: 7000000,
      imageUrl: 'https://images.pexels.com/photos/364822/rolex-watch-time-luxury-364822.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1/150x150'
