import { Component, Output, EventEmitter } from '@angular/core';



@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  products = [
    { id: 1, name: 'Đồng hồ Rolex', description: 'Đồng hồ dành cho giới thượng lưu', price: 100000000 },
    { id: 2, name: 'Đồng hồ Casio', description: 'Đồng hồ dành cho giới trẻ', price: 1000000 },
    { id: 3, name: 'Đồng hồ Seiko', description: 'Đồng hồ dành cho giới trung niên', price: 5000000 }
  ];

  addProduct(newProduct: any) {
    const newId = this.products.length + 1;
    const product = { id: newId, ...newProduct };
    this.products.push(product);
  }

}
