import { Component } from '@angular/core';

@Component({
  selector: 'app-spham',
  templateUrl: './spham.component.html',
  styleUrls: ['./spham.component.css']
})
export class SphamComponent {
  watches: Watch[] = [
    { id: 1, name: 'Rolex Submariner', brand: 'Rolex', price: 10000 },
    { id: 2, name: 'Omega Speedmaster', brand: 'Omega', price: 5000 },
    { id: 3, name: 'Tag Heuer Monaco', brand: 'Tag Heuer', price: 7000 },
  ];

  constructor() { }

  getWatches(): Watch[] {
    return this.watches;
  }

  getWatch(id: number): Watch {
    return this.watches.find(watch => watch.id === id);
  }

  addWatch(watch: Watch): void {
    watch.id = this.watches.length + 1;
    this.watches.push(watch);
  }

  updateWatch(watch: Watch): void {
    const index = this.watches.findIndex(w => w.id === watch.id);
    this.watches[index] = watch;
  }

  deleteWatch(watch: Watch): void {
    const index = this.watches.findIndex(w => w.id === watch.id);
    this.watches.splice(index, 1);
  }
}
