import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
@Component({
  selector: 'app-danhmuc',
  templateUrl: './danhmuc.component.html',
  styleUrls: ['./danhmuc.component.css']
})
export class DanhmucComponent {
  registrationForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.registrationForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      phone: ['', Validators.required],
      address: ['', Validators.required],
      product: ['', Validators.required],
      quantity: ['', Validators.required]
    });
  }

  onSubmit() {
    console.log(this.registrationForm.value);
  }


}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
  <script>
    const form = document.querySelector('#product-form');
    const table = document.querySelector('#product-table tbody');

    form.addEventListener('submit', (event) => {
      // Prevent default form submission
      event.preventDefault();

      // Get form values
      const name = document.querySelector('#name').value;
      const price = document.querySelector('#price').value;
      const quantity = document.querySelector('#quantity').value;
      const image = document.querySelector('#image').value;

      // Create new table row with product information
      const newRow = table.insertRow();
      const nameCell = newRow.insertCell(0);
      const priceCell = newRow.insertCell(1);
      const quantityCell = newRow.insertCell(2);
      const imageCell = newRow.insertCell(3);
      nameCell.textContent = name;
      priceCell.textContent = price;
      quantityCell.textContent = quantity;
      if (image) {
        const img = document.createElement('img');
        img.src = image;
        img.alt = name;
        img.width = 100;
        imageCell.appendChild(img);
      }

      // Reset form
      form.reset();
    });
  </script>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }


  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>


  Với form này, người dùng sẽ được yêu cầu nhập thông tin của họ bao gồm họ tên, email, số điện thoại, địa chỉ, tên sản phẩm và số lượng sản phẩm mà họ muốn đăng ký mua. Khi người dùng nhấn nút "Đăng ký", thông tin sẽ được gửi đến hàm `onSubmit()` để xử lý. Trong ví dụ này, chúng ta chỉ đơn giản là in thông tin đăng ký ra console. Bạn có thể thay đổi hàm `onSubmit()` để thực hiện việc gửi thông tin đăng ký đến API hoặc lưu trữ trong cơ sở dữ liệu.
