import { Component } from '@angular/core';
interface Product {
  name: string;
  description: string;
  price: number;
  image: string;
}
@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  products: Product[] = [{
    name: "Đồng hồ 1",
    description: "Mô tả đồng hồ 1",
    price: 1000000,
    image: "https://via.placeholder.com/300x200?text=Product+Image+1"
  }, {
    name:"Đồng hồ 2",
    description: "Mô tả đồng hồ 2",
    price: 2000000,
    image: "https://via.placeholder.com/300x200?text=Product+Image+2"
  }, {
    name: "Đồng hồ 3",
    description: "Mô tả đồng hồ 3",
    price: 3000000,
    image: "https://via.placeholder.com/300x200?text=Product+Image+3"
  }];

  newProduct: Product = {
    name: '',
    description: '',
    price: 0,
    image: ''
  };

  addProduct(productForm: any) {
    if (productForm.valid) {
      this.products.push(this.newProduct);
      this.newProduct = {
        name: '',
        description: '',
        price: 0,
        image: ''
      };
    }
  }

  deleteProduct(product: Product) {
    const index = this.products.indexOf(product);
    if (index !== -1) {
      this.products.splice(index, 1);
    }
}
}
