import { Component} from '@angular/core';


@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  showForm = false;
  watch = { name: '', price: 0, image: '' };

  toggleForm() {
    this.showForm = !this.showForm;
  }

  addWatch() {
    // Thêm sản phẩm vào cơ sở dữ liệu
    // Sau đó reset form và ẩn nó đi
    this.watch = { name: '', price: 0, image: '' };
    this.showForm = false;
  }
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="add-watch">
    <button (click)="toggleForm()">{{ showForm ? 'Đóng' : 'Thêm sản phẩm' }}</button>
    <div class="form" *ngIf="showForm">
      <h3>Thêm sản phẩm mới</h3>
      <form>
        <label for="name">Tên sản phẩm:</label>
        <input type="text" id="name" [(ngModel)]="watch.name">
        <label for="price">Giá tiền:</label>
        <input type="number" id="price" [(ngModel)]="watch.price">
        <label for="image">Hình ảnh:</label>
        <input type="text" id="image" [(ngModel)]="watch.image">
        <button type="button" (click)="addWatch()">Thêm sản phẩm</button>
      </form>
    </div>
  </div>
<body>
<html>
