import { Component} from '@angular/core';

@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  products = [
    { name: 'Sản phẩm 1', price: 100, image: 'product1.jpg' },
    { name: 'Sản phẩm 2', price: 200, image: 'product2.jpg' },
    { name: 'Sản phẩm 3', price: 300, image: 'product3.jpg' }
  ];
  newProduct = { name: '', price: 0, image: '' };
  showForm = false;

  toggleForm() {
    this.showForm = !this.showForm;
  }

  addProduct() {
    this.products.push(this.newProduct);
    this.newProduct = { name: '', price: 0, image: '' };
    this.showForm = false;
  }
}
