import { Component } from '@angular/core';




interface Watch {
  name: string;
  price: number;
  description: string;
  image: string;
}

@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  currentWatch: Watch = {
    name: '',
    price: 0,
    description: '',
    image: ''
  };

  constructor(private watchService: WatchService) {}

  saveWatch() {
    this.watchService.addWatch(this.currentWatch).subscribe(
      () => {
        console.log('Watch added successfully');
      },

        console.log('Error adding watch: ', error);
      }
    );
  }

  cancelWatch() {
    this.currentWatch = {
      name: '',
      price: 0,
      description: '',
      image: ''
    };
  }

  handleImageChange(event: any) {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
      this.currentWatch.image = reader.result as string;
    };
}
}
