import { Component } from '@angular/core';



@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  watchForm: FormGroup;

  constructor(private fb: FormBuilder) { }

  ngOnInit(): void {
    this.watchForm = this.fb.group({
      brand: ['', Validators.required],
      model: ['', Validators.required],
      price: ['', [Validators.required, Validators.min(0)]],
      imageUrl: ['', Validators.required]
    });
  }

  onSubmit() {
    const newWatch = new Watch(
      this.watchForm.value.brand,
      this.watchForm.value.model,
      this.watchForm.value.price,
      this.watchForm.value.imageUrl
    );
    this.watchForm.reset();
    this.watchForm.markAsPristine();
    this.watchForm.markAsUntouched();
    // emit new watch to parent component
  }
}
