import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
	<script type="text/javascript">
        const productForm = document.getElementById('product-form');
const productIdInput = document.getElementById('product-id');
const productCodeInput = document.getElementById('product-code');
const productQuantityInput = document.getElementById('product-quantity');
const productStatusInput = document.getElementById('product-status');
const productPriceInput = document.getElementById('product-price');
const productCategoryInput = document.getElementById('product-category');
const productTableBody = document.getElementById('product-table-body');

let products = []; // Mảng chứa danh sách sản phẩm

// Hàm tạo một sản phẩm mới và thêm vào mảng products
function createProduct(code, quantity, status, price, category) {
    const product = {
        id: Date.now(),
        code,
        quantity,
        status,
        price,
        category
    };
    products.push(product);
    return product;
}

// Hàm lấy sản phẩm từ mảng products theo id
function getProductById(id) {
    return products.find(product => product.id === id);
}

// Hàm xóa sản phẩm từ mảng products theo id
function deleteProductById(id) {
    products = products.filter(product => product.id !== id);
}

// Hàm hiển thị danh sách sản phẩm lên table
function renderProductTable() {
    productTableBody.innerHTML = '';
    products.forEach(product => {
        const row = document.createElement('tr');
        row.innerHTML = `
            <td>${product.code}</td>
            <td>${product.quantity}</td>
            <td>${product.status}</td>
            <td>${product.price}</td>
            <td>${product.category}</td>
            <td>
                <button class="edit-button" data-id="${product.id}">Sửa</button>
                <button class="delete-button" data-id="${product.id}">Xóa</button>
            </td>
        `;
        productTableBody.appendChild(row);
    });
}

// Hàm reset form
function resetProductForm() {
    productIdInput.value = '';
    productCodeInput.value = '';
    productQuantityInput.value = '';
    productStatusInput.value = 'new';
    productPriceInput.value = '';
    productCategoryInput.value = '';
}

// Sự kiện submit form
productForm.addEventListener('submit', event => {
    event.preventDefault();

    const code = productCodeInput.value.trim();
    const quantity = parseInt(productQuantityInput.value.trim());
    const status = productStatusInput.value;
    const price = parseInt(productPriceInput.value.trim());
    const category = productCategoryInput.value.trim();

    if (!code || !quantity || !price || !category) {
        alert('Vui lòng nhập đầy đủ thông tin sản phẩm');
        return;
    }

    let product;
    if (productIdInput.value) {
        // Nếu productIdInput có giá trị => đang ở chế độ sửa sản phẩm
        const id = parseInt(productIdInput.value);
        product = getProductById(id);
        if (product) {
            product.code = code;
            product.quantity = quantity;
            product.status = status;
            product.price = price;
            product.category = category;
        }
    } else {
        // Ngược lại là chế độ thêm sản phẩm mới
        product = createProduct(code, quantity, status, price, category);
    }

    renderProductTable();
    resetProductForm();
});

// Sự kiện click vào nút Sửa hoặc Xóa sản phẩm
productTableBody.addEventListener('click', event => {
    if (event.target.classList.contains('edit-button')) {
        const id = parseInt(event.target.getAttribute('data-id'));
        const product = getProductById(id);
        if (product) {
            productIdInput.value = product.id;
            productCodeInput.value = product.code;
            productQuantityInput.value = product.quantity;
            productStatusInput.value = product.status;
            productPriceInput.value = product.price;
            productCategoryInput.value = product.category;
        }
    } else if (event.target.classList.contains('delete-button')) {
        const id = parseInt(event.target.getAttribute('data-id'));
        deleteProductById(id);
        renderProductTable();
    }
});

// Sự kiện click vào nút Hủy
document.getElementById('cancel-button').addEventListener('click', () => {
    resetProductForm();
});

// Sự kiện click vào nút In danh sách sản phẩm
document.getElementById('print-button').addEventListener('click', () => {
    window.print();
});

// Hiển thị danh sách sản phẩm lên table khi trang web được load
renderProductTable();
    </script>
