  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>

	<div class="container">
        <h1>Danh sách sản phẩm bán đồng hồ</h1>
        <div class="form-container">
            <form id="product-form">
                <input type="hidden" id="product-id">
                <div class="form-group">
                    <label for="product-code">Mã sản phẩm:</label>
                    <input type="text" id="product-code" required>
                </div>
                <div class="form-group">
                    <label for="product-quantity">Số lượng:</label>
                    <input type="number" id="product-quantity" required>
                </div>
                <div class="form-group">
                    <label for="product-status">Tình trạng:</label>
                    <select id="product-status" required>
                        <option value="new">Mới</option>
                        <option value="used">Đã sử dụng</option>
                    </select>
                </div>
                <div class="form-group">
                    <label for="product-price">Giá tiền:</label>
                    <input type="number" id="product-price" required>
                </div>
                <div class="form-group">
                    <label for="product-category">Danh mục:</label>
                    <input type="text" id="product-category" required>
                </div>
                <div class="form-group">
                    <button type="submit" id="save-button">Lưu</button>
                    <button type="button" id="cancel-button">Hủy</button>
                </div>
            </form>
        </div>
        <div id="product-list">
            <table>
                <thead>
                    <tr>
                        <th>Mã sản phẩm</th>
                        <th>Số lượng</th>
                        <th>Tình trạng</th>
                        <th>Giá tiền</th>
                        <th>Danh mục</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody id="product-table-body">
                    <!-- Danh sách sản phẩm sẽ được tạo ở đây -->
                </tbody>
            </table>
            <button id="print-button">In danh sách sản phẩm</button>
        </div>
    </div>
    <script src="script.js"></script>
