import { Component } from '@angular/core';

@Component({
  selector: 'app-spham',
  templateUrl: './spham.component.html',
  styleUrls: ['./spham.component.css']
})
export class SphamComponent {
  watch: Watch = {
    id: 0,
    name: '',
    brand: '',
    price: 0
  };
  isEdit = false;

  constructor(private watchService: WatchService, private router: Router, private route: ActivatedRoute) { }

  ngOnInit(): void {
    const id = parseInt(this.route.snapshot.paramMap.get('id') || '0');
    if (id) {
      this.watch = this.watchService.getWatch(id);
      this.isEdit = true;
    }
  }

  onSubmit(): void {
    if (this.isEdit) {
      this.watchService.updateWatch(this.watch);
    } else {
      this.watchService.addWatch(this.watch);
    }
    this.router.navigate(['/watches']);
  }

  onCancel(): void {
    this.router.navigate(['/watches']);
  }
}
