import { Component, OnInit } from '@angular/core';



@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
export class QlspComponent {

}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }





  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <h2>Quản lý sản phẩm đồng hồ</h2>
  <h2>Product List</h2>
  <h2>Product List</h2>






  <h2>Product List</h2>

<table>
  <thead>
    <tr>
      <th>Id</th>
      <th>Name</th>
      <th>Quantity</th>
      <th>Status</th>
      <th>Price</th>
      <th>Category</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let product of products">
      <td>{{ product.id }}</td>
      <td>{{ product.name }}</td>
      <td>{{ product.quantity }}</td>
      <td>{{ product.status }}</td>
      <td>{{ product.price }}</td>
      <td>{{ product.category }}</td>
      <td>
        <button (click)="deleteProduct(product.id)">Delete</button>
        <button (click)="editProduct(product)">Edit</button>
      </td>
    </tr>
  </tbody>
</table>

<h2>Add Product</h2>

<form (submit)="addProduct()">
  <div>
    <label for="name">Name:</label>
    <input id="name" name="name" [(ngModel)]="newProduct.name" required>
  </div>
  <div>
    <label for="quantity">Quantity:</label>
    <input id="quantity" name="quantity" type="number" [(ngModel)]="newProduct.quantity" required>
  </div>
  <div>
    <label for="status">Status:</label>
    <select id="status" name="status" [(ngModel)]="newProduct.status" required>
      <option value="Active">Active</option>
      <option value="Inactive">Inactive</option>
    </select>
  </div>
  <div>
    <label for="price">Price:</label>
    <input id="price" name="price" type="number" [(ngModel)]="newProduct.price" required>
  </div>
  <div>
    <label for="category">Category:</label>
    <input id="category" name="category" [(ngModel)]="newProduct.category" required>
  </div>
  <button type="submit">Add Product</button>
</form>
<body>
<html>
