import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  productId: number;
  productName: string;
  productPrice: number;

  createProduct() {
    // Gửi yêu cầu tạo mới sản phẩm lên server
    console.log(`Tạo sản phẩm mới: ID=${this.productId}, Tên=${this.productName}, Giá=${this.productPrice}`);
  }
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 100px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }








      h1 {
        text-align: center;
      }

      form {
        margin: auto;
        width: 50%;
      }

      div {
        margin-bottom: 10px;
      }

      label {
        display: inline-block;
        width: 100px;
        text-align: right;
        margin-right: 10px;
      }

      input[type=number], input[type=text] {
        width: 200px;
      }

      button[type=submit] {
        display: block;
        margin: auto;
      }
  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>




  <h1>Tạo sản phẩm mới</h1>
<form>
  <div>
    <label for="productId">ID:</label>
    <input type="number" id="productId" name="productId" [(ngModel)]="productId" required>
  </div>
  <div>
    <label for="productName">Tên sản phẩm:</label>
    <input type="text" id="productName" name="productName" [(ngModel)]="productName" required>
  </div>
  <div>
    <label for="productPrice">Giá:</label>
    <input type="number" id="productPrice" name="productPrice" [(ngModel)]="productPrice" required>
  </div>
  <button type="submit" (click)="createProduct()">Tạo sản phẩm mới</button>
</form>
<body>
<html>
