import { Component } from '@angular/core';



@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
styles: [`
form {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  max-width: 500px;
}
label {
  font-weight: bold;
}
input, textarea {
  padding: 0.5rem;
  border-radius: 3px;
  border: 1px solid #ccc;
  font-size: 1rem;
}
button[type="submit"] {
  padding: 0.5rem;
  background-color: #4CAF50;
  color: white;
  border-radius: 3px;
  border: none;
  cursor: pointer;
  font-size: 1rem;
}
`]
})
